import React, { useState, useEffect } from "react";
import "./Form.css";

const Form = () => {
  const [task, setTask] = useState("");
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setTasks([...tasks, task]);
    setTask("");
  };

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleDelete = (index) => {
    setTasks(tasks.filter((task, i) => i !== index));
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="form-title">
        <div className="input-group">
          <input
            type="text"
            name="name"
            placeholder="What's on your list today..."
            value={task}
            onChange={handleChange}
            className="adding-a-task-list"
          />
          <button type="submit" className="add-button">
            Add
          </button>
        </div>
      </form>
      <ul className="task-list">
        {tasks.map((task, index) => (
          <li key={index} className="task-list-item">
            {task}
            <button onClick={() => handleDelete(index)} class="delete-button">
  <svg class="trash-icon" viewBox="0 0 24 24">
    <path fill="#fff" d="M8.5,20h7c0.3,0,0.5-0.2,0.5-0.5v-10c0-0.3-0.2-0.5-0.5-0.5h-7C8.2,9,8,9.2,8,9.5v10C8,19.8,8.2,20,8.5,20z M18.5,4h-3.3L14.2,3c-0.2-0.4-0.5-1-1.2-1h-3c-0.7,0-1,0.6-1.2,1l-1,1H5.5C4.7,4,4,4.7,4,5.5v1c0,0.8,0.7,1.5,1.5,1.5h13   c0.8,0,1.5-0.7,1.5-1.5v-1C20,4.7,19.3,4,18.5,4z"/>
  </svg>
</button>

          </li>
        ))}
      </ul>
    </div>
  );
};

export default Form;
