import React, { useState } from "react";
import "./App.css"

const Form = () => {
  // Use an array to store the tasks instead of input
  const [task, setTask] = useState("");
  const [tasks, setTasks] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setTasks([...tasks, task]); // Add the task to the tasks array
    setTask(""); // Clear the input field
  };

  // The handleClick function is not needed

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  return (
    <div>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          name="name"
          placeholder="What's on your list today..."
          value={task}
          onChange={handleChange}
          className=""
        />
        <button type="submit" className="add-button">Add</button>{" "}
        {/* Use type="submit" to trigger the handleSubmit function */}
      </form>
      {/* Display the tasks */}
      <ul className="unordered-list">
        {tasks.map((task, index) => (
          <li key={index} className="task-list">{task}</li>
        ))}
      </ul>
    </div>
  );
};

export default Form;
