import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import calendarIcon from "./calendar-icon.png";

const ReminderComponent = ({ selectedDate, handleDateChange }) => {
  const [startDate, setStartDate] = useState(selectedDate);

  const handleChange = (date) => {
    setStartDate(date);
    if (typeof handleDateChange === "function") {
      handleDateChange(date);
    }
  };

  const CustomInput = ({ value, onClick }) => (
    <div className="custom-datepicker-input" onClick={onClick}>
      <img src={calendarIcon} alt="Calendar icon" />
      <span>{value || "Select a date and time"}</span>
    </div>
  );

  return (
    <div className="reminder-component">
      <label className="reminder-label">Set a reminder:</label>
      <DatePicker
        className="reminder-datepicker"
        selected={startDate}
        onChange={handleChange}
        showTimeSelect
        timeFormat="HH:mm"
        timeIntervals={15}
        dateFormat="dd/MM/yyyy h:mm aa"
        minDate={new Date()}
        isClearable
        customInput={<CustomInput />}
      />
    </div>
  );
};

export default ReminderComponent;
