import React from 'react'
// import {v4 as uuidv4} from "uuid"

const Form = ({input ,setInput,task,setTask}) => {
  function onInputChange(e) {
    setInput(e.target.value)
  }
  function handleSubmit(e) {
    e.preventDefault()
    setTask(input)


  }
  return (
  <form onSubmit={handleSubmit}>
    <input type="text" placeholder="What's on your list today ..." className='task-input' value={input} required onChange={onInputChange}/>
    <button className='button-add' type='submit'></button>
  </form>
  )
}

export default Form
