import React, { useState, useEffect } from "react";
import "./Form.css";

const Form = () => {
  const [task, setTask] = useState("");
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    const storedTasks = localStorage.getItem("tasks");
    if (storedTasks) {
      setTasks(JSON.parse(storedTasks));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("tasks", JSON.stringify(tasks));
  }, [tasks]);

  const handleSubmit = (e) => {
    e.preventDefault();
    setTasks([...tasks, task]);
    setTask("");
  };

  const handleChange = (e) => {
    setTask(e.target.value);
  };

  const handleDelete = (index) => {
    setTasks(tasks.filter((task, i) => i !== index));
  };

  return (
    <div className="form-container">
      <form onSubmit={handleSubmit} className="form-title">
        <div className="input-group">
          <input
            type="text"
            name="name"
            placeholder="What's on your list today..."
            value={task}
            onChange={handleChange}
            className="adding-a-task-list"
          />
          <button type="submit" className="add-button">
            Add
          </button>
        </div>
      </form>
      <ul className="task-list">
        {tasks.map((task, index) => (
          <li key={index} className="task-list-item">
            {task}
            <button onClick={() => handleDelete(index)} class="delete-button">
              <svg className="trash-icon" viewBox="0 0 24 24">
                <path d="M19 7H17V4C17 2.9 16.1 2 15 2H9C7.9 2 7 2.9 7 4V7H5C3.9 7 3 7.9 3 9V20C3 21.1 3.9 22 5 22H19C20.1 22 21 21.1 21 20V9C21 7.9 20.1 7 19 7ZM9 4H15V7H9V4ZM19 20H5V9H19V20ZM12 14C12.6 14 13 14.4 13 15V17C13 17.6 12.6 18 12 18C11.4 18 11 17.6 11 17V15C11 14.4 11.4 14 12 14Z" />
              </svg>
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Form;
