import React, { useState } from "react";
import { Link } from "react-router-dom";
import { FaHome, FaInfoCircle, FaBriefcase, FaEnvelope } from "react-icons/fa";
import "./Navbar.css";

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleNavbar = () => {
    setIsOpen(!isOpen);
  };

  return (
    <nav className={`navbar ${isOpen ? "open" : ""}`}>
      <div className="navbar-container">
        <div className="navbar-toggle" onClick={toggleNavbar}>
          <div className="line"></div>
          <div className="line"></div>
          <div className="line"></div>
        </div>
        <ul>
          <li className="home-page">
            <Link to="/">
              <span className="nav-link">
                <FaHome />

              </span>
            </Link>
          </li>
          {/* <li className="login-page">
            <Link to="/login">Login</Link>
          </li> */}
          <li className="todo-page">
            <Link to="/todo">MY TO-DO LIST</Link>
          </li>
          <li>
            <Link to="/terms_policies_and_privacy"></Link>
          </li>
          <li>
            <Link to="/about">
              <FaInfoCircle /> ABOUT
            </Link>
          </li>
          <li>
            <Link to="/services">
              <FaBriefcase /> SERVICES
            </Link>
          </li>
          <li>
            <Link to="/contact">
              <FaEnvelope /> CONTACT
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
